-- 更新军装的说明信息
UPDATE item_template SET description = 
	CONCAT('需要军衔：',
	(SELECT ts.`content_default` FROM trinity_string ts WHERE ts.`entry` = (60100 + army_rank)),
	'，对应的军衔等级：',
	army_rank)
WHERE 1=1
AND (`name` LIKE '角斗士的%' OR `name` LIKE '残酷角斗士的%' OR `name` LIKE '复仇角斗士的%' OR `name` LIKE '野蛮角斗士的%')
AND (itemLevel BETWEEN 101 AND 199) 
AND ((`class` = 4 AND `InventoryType` IN(10,5,20,7,1,3,14,23,28)) OR `class` = 2) 
AND entry <1000000;


